-- Seed Fitness Clubs
-- Source: OpenStreetMap Overpass API
-- Generated: 2025-12-06

-- First, ensure the Fitness Clubs layer exists
SELECT 'Fitness Clubs', 'Фітнес-клуби', 'fitness', 'dumbbell', '#FF5722'
WHERE NOT EXISTS (SELECT 1 FROM poi_layers WHERE name = 'Fitness Clubs');

DO $$
DECLARE
    fitness_layer_id UUID;
    kyiv_id UUID;
    lviv_id UUID;
    kharkiv_id UUID;
    odesa_id UUID;
    dnipro_id UUID;
BEGIN
    SELECT id INTO fitness_layer_id FROM poi_layers WHERE name = 'Fitness Clubs' LIMIT 1;

    -- Verify layer exists
    IF fitness_layer_id IS NULL THEN
        RAISE EXCEPTION 'Fitness Clubs layer not found in poi_layers table';
    END IF;

    SELECT id INTO kyiv_id FROM cities WHERE name = 'Kyiv' LIMIT 1;
    SELECT id INTO lviv_id FROM cities WHERE name = 'Lviv' LIMIT 1;
    SELECT id INTO kharkiv_id FROM cities WHERE name = 'Kharkiv' LIMIT 1;
    SELECT id INTO odesa_id FROM cities WHERE name = 'Odesa' LIMIT 1;
    SELECT id INTO dnipro_id FROM cities WHERE name = 'Dnipro' LIMIT 1;

    ON CONFLICT DO NOTHING;

    RAISE NOTICE 'Inserted fitness clubs';
END $$;